       const randomNumber = Math.random();
       if (randomNumber > 0.5) {
         resolve('API response data');
       } else {
         reject(new Error('Failed API response'));
       }
     };

     setTimeout(ApiResponse, 1000);
  });
 }

 export default getResponseFromAPI;

